    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
       String bookId = jTextField2.getText();
    String name = jTextField1.getText();
    String imagePath = jTextField4.getText();
    String publisher = jTextField3.getText();
    String branch = (String) jComboBox1.getSelectedItem();
    try {
        Connection con = ConectionProvider.getCon();
        Statement st = con.createStatement();
        // Get the selected image file path
       
        // Read the binary data of the image file
        File imageFile = new File(imagePath);
        FileInputStream fis = new FileInputStream(imageFile);
        // Use PreparedStatement to insert image data into the database
        PreparedStatement pstmt = con.prepareStatement("INSERT INTO book VALUES (?, ?, ?, ?, ?)");
        pstmt.setString(1, bookId);
        pstmt.setString(2, name);
        pstmt.setString(3, publisher);
        pstmt.setString(4, branch);
        // Set the binary data of the image
        pstmt.setBinaryStream(5, fis, (int) imageFile.length());
        pstmt.executeUpdate();
        JOptionPane.showMessageDialog(null, "Successfully added book");
      
    } catch (Exception e) {
        JOptionPane.showMessageDialog(null, "Error adding book: " + e.getMessage());
        
    }
    }//GEN-LAST:event_jButton1ActionPerformed

