    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
     // TODO add your handling code here:
    JFileChooser fileChooser = new JFileChooser();
    fileChooser.setDialogTitle("Choose an image");
    
    // Show the file chooser dialog
    int result = fileChooser.showOpenDialog(this);
    
    // Check if a file was selected
    if (result == JFileChooser.APPROVE_OPTION) {
        File selectedFile = fileChooser.getSelectedFile();
        
        // Set the path of the selected file to the imagePath variable
        String imagePath = selectedFile.getAbsolutePath();
        
        // Now you can directly use imagePath to save it in your database
        jTextField4.setText(imagePath); // If you want to display the path in the JTextField
    }

    }//GEN-LAST:event_jButton2ActionPerformed

